// }) => (
//     <MuiPickersUtilsProvider utils={DateFnsUtils}>
//       <KeyboardDatePicker
//         disableToolbar
//         {...input}
//         variant="inline"
//         label="Fecha de nacimiento"
//         margin="normal"
//         format="MM/dd/yyyy"
//         KeyboardButtonProps={{
//           'aria-label': 'change date',
//           'size': 'small'
//         }}
//         disableFuture={true}
//         autoOk={true}
//         error={(touched && error) ? true : false}
//         style={{ width: "100%" }}
//         helperText={touched && error}
//       />
//     </MuiPickersUtilsProvider>

//   )


export const ReduxFormSelect = props => {
  const customStyles = {
      option: (provided, state) => ({
          ...provided,
          fontSize: 13
      }),
      control: styles => ({ ...styles, backgroundColor: 'white', fontSize: 13, fontFamily: 'sans-serif' }),
      singleValue: (provided, state) => {
          const opacity = state.isDisabled ? 0.5 : 1;
          const transition = 'opacity 300ms';
          return { ...provided, opacity, transition };
      }
  }
  const { input, options } = props;
  const { touched, error } = props.meta;
  return (
      <>
          <Select
              {...input}

              styles={customStyles}
              isSearchable={false}
              placeholder='Seleccione un modulo'
              onChange={value => input.onChange(value)}
              onBlur={() => input.onBlur(input.value)}
              noOptionsMessage={() => 'Aun no hay ningun modulo registrado'}
              options={options}
          />
          {touched && ((error && <span className="text-danger form-group" style={{ fontSize: '12px', fontFamily: 'sans-serif' }}>{error}</span>))}
      </>
  )
}

const CssTextField = withStyles({
  root: {
    '& label.Mui-focused': {
      color: 'black',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: 'gray',
        fontSize:'13px'
      }
    },
  },
})(TextField);


const styles = {
  inputRoot: {
    fontSize: 13
  },
  labelRoot: {
    fontSize: 13,
    color: "gray",
    heigth: '3px',
    "&$labelFocused": {
      color: "purple",
      fontSize: 16
    }
  },
  labelFocused: {}
};


const stylesDate = {
  inputRoot: {
    fontSize: 14
  },
  labelRoot: {
    fontSize: 15,
    color: "gray",
    heigth: '3px',
    "&$labelFocused": {
      color: "purple",
      fontSize: 10
    }
  },
  labelFocused: {}
};


function renderAlgo({
  input,
  label, classes,filas,
  meta: { touched, error },
  ...custom
}) {
  return (
    <TextField
      fullWidth
      label={label}
      InputProps={{ classes: { root: classes.inputRoot } }}
      InputLabelProps={{
        classes: {
          root: classes.labelRoot,
          focused: classes.labelFocused
        }
      }}
      error={(touched && error) ? true : false}
      helperText={touched && error}
      required={true}
      {...input}
      {...custom}
      variant='outlined'
      margin="normal"
      size='small'
    />
  )
}

function renderDate({
  input,
  label, classes,
  meta: { touched, error },
  ...custom
}) {
  return (
    <TextField
      fullWidth
      label={label}
      InputProps={{ classes: { root: classes.inputRoot } }}
      InputLabelProps={{
        classes: {
          root: classes.labelRoot,
          focused: classes.labelFocused
        },
        shrink: true,
      }}
      error={(touched && error) ? true : false}
      helperText={touched && error}
      required={true}
      {...input}
      {...custom}
      variant='outlined'
      margin="normal"
      size='small'
    />
  )
}

function renderArea({
  input,filas,
  label, classes,
  meta: { touched, error },
  ...custom
}) {
  return (
    <TextField
      fullWidth
      label={label}
      InputProps={{ classes: { root: classes.inputRoot } }}
      InputLabelProps={{
        classes: {
          root: classes.labelRoot,
          focused: classes.labelFocused
        },
        shrink: true,
      }}
      error={(touched && error) ? true : false}
      helperText={touched && error}
      required={true}
      {...input}
      {...custom}
      multiline={true}
      rows={filas}
      variant='outlined'
      margin="normal"
      size='small'
    />
  )
}

export const generarInput = withStyles(styles)(renderAlgo);
export const generarDate = withStyles(stylesDate)(renderDate);
export const generarArea=withStyles(styles)(renderArea);
