import React from 'react';

//estilos
import '../../css/business-casual.css'
import '../../css/estilos.css'
import '../../css/bootstrap.min.css'
import '../../css/menu.css'


//componentes
import Barra from '../general/BarraDirecciones.js'
import PopUpUsuario from '../popup/PopUpUsuario.js'
import Fila from '../general/FilaTablaUsuario.js'
import MaterialTable from 'material-table';
import MTableToolbar from '../../utilitario/MTableToolbar.js';
import { confirmAlert } from 'react-confirm-alert'; // Import

import { NotificationManager } from 'react-notifications';

//redux conexion
import { connect } from 'react-redux';
import { actionConsultarUsuarios, actionAsignarCedula, actualizarMensajeEditar, actionActualizarUsuarios } from '../../actions/actionsUsuario.js'
import { withRouter } from 'react-router-dom';

class ContenidoAdminUsuario extends React.Component {

	state={
		habilitar:false
	}

	cambiarEstado(valor){
		this.setState({habilitar:valor});
	}

	componentDidMount() {
		this.props.actionConsultarUsuarios(localStorage.getItem('Token'),this.cambiarEstado);
	}

	renderTableData() {
		return this.props.usuarios.map((usuario, index) => {
			const { cedula } = usuario //destructuring
			return (
				<Fila usuario={usuario} cambiar={this.props.cambiar} key={cedula} />
			)
		})

	}

	activarDesactivarUsuario(cedula) {
		confirmAlert({
			title: '',
			message: '¿Esta seguro?',
			buttons: [
				{
					label: 'Si',
					onClick: () => {
						let nuevo = [];
						this.props.usuarios.map(function (task, index, array) {
							if (task.numeroDocumento === cedula) {
								if (task.estado === "Suspendido") {
									let usuario = {
										nombre: task.nombre,
										numeroDocumento: task.numeroDocumento,
										correo: task.correo,
										estado: "Activo"
									}
									nuevo.push(usuario);
								} else {
									let usuario = {
										nombre: task.nombre,
										numeroDocumento: task.numeroDocumento,
										correo: task.correo,
										estado: "Suspendido"
									}
									nuevo.push(usuario);
								}
							} else {
								nuevo.push(task);
							}
						});
						this.props.actionActualizarUsuarios(nuevo);
					}
				},
				{
					label: 'No',
					onClick: () => NotificationManager.info('Se cancelo la operacion')
				}
			]
		});

	}

	render() {
		return (
			<div>
				<div className="text-left titulo" style={estiloLetrero}>
					<h4>Administrar usuarios</h4>
				</div>
				<Barra texto="Inicio > Administración de usuarios" />
				<div className="col-sm-12" style={{
					paddingTop: "20px",
					paddingRight: "46px",
					paddingLeft: "40px",
					paddingBottom: "7px",
				}}>
				</div>
				<div className="container" style={{
					paddingTop: "7px",
					paddingRight: "12px",
					paddingLeft: "40px",
					paddingBottom: "20px",
					margin: "0px 0px 32px"
				}}>
					<div className="container shadow" style={fondoBarraSuperior}>
						<div>
							<MaterialTable
								title=""
								localization={{
									header: {
										actions: ' '
									},
									pagination: {
										nextTooltip: 'Siguiente ',
										previousTooltip: 'Anterior',
										labelDisplayedRows: '{from}-{to} de {count}',
										lastTooltip: 'Ultima pagina',
										firstTooltip: 'Primera pagina',
										labelRowsSelect: 'Registros',
										firstAriaLabel: 'oooo'
									},
									body: {
										emptyDataSourceMessage: 'Aun no hay ningun usuario registrado'
									},
									toolbar: {
										searchTooltip: 'Buscar',
										searchPlaceholder: 'Buscar'
									}
								}}
								columns={[
									{
										title: 'Numero de identificacion', field: 'numeroDocumento', type: 'numeric',
										headerStyle: estiloCabecera,
										cellStyle: estiloFila
									},
									{ title: 'Nombre', field: 'nombre', headerStyle: estiloCabecera, cellStyle: estiloFila },
									{ title: 'Correo electronico', field: 'correoElectronico', headerStyle: estiloCabecera, cellStyle: estiloFila },
									{
										title: 'Estado', field: 'estado',
										render: rowData => {
											if (rowData.estado === 'Suspendido') {
												return <span className="label label-sm letra"
													style={{
														textShadow: "none!important",
														fontSize: "12px",
														fontFamily: "Open Sans,sans-serif",
														fontWeight: "300",
														padding: "3px 6px",
														color: "#fff",
														background: "#ED6B75"
													}}>{rowData.estado}</span>
											} else {
												return <span className="label label-sm letra"
													style={{
														textShadow: "none!important",
														fontSize: "12px",
														fontFamily: "Open Sans,sans-serif",
														fontWeight: "300",
														padding: "3px 6px",
														color: "#fff",
														background: "#408725"
													}}>{rowData.estado}</span>
											}
										},
										headerStyle: estiloCabecera, cellStyle: estiloFila
									},
								]}
								data={this.props.usuarios}
								options={{
									search: true,
									rowStyle: estiloFila

								}}
								actions={[
									{
										icon: 'edit',
										tooltip: 'Editar informacion',
										onClick: (event, rowData) => {
											this.props.actionAsignarCedula(rowData.numeroDocumento);
											this.props.history.push('/editarUsuario');
										}
									},
									{
										icon: 'restore',
										tooltip: 'Suspender / Activar',
										onClick: (event, rowData) => this.activarDesactivarUsuario(rowData.numeroDocumento)
									},
									{
										icon: 'assignmentInd',
										tooltip: 'Asignar actividad',
										onClick: (event, rowData) => alert("You saved " + rowData.numeroDocumento)
									}
								]}

								components={{
									Toolbar: props => (
										<div className="row">
											<div className="col-sm-4">
												<div style={{ padding: '16px' }}>
													<PopUpUsuario funcion={this.anadirTarea} />
												</div>
											</div>
											<div className="col-sm-8">
												<MTableToolbar {...props} />
											</div>
										</div>
									),
								}}

							/>
						</div>
					</div>
				</div>
			</div>
		);
	}


}

const estiloCabecera = {
	fontSize: '13px',
	fontFamily: 'sans-serif',
	padding: '8px',
	background: '#e7ecf1'

}

const estiloFila = {
	fontSize: '12px',
	fontFamily: 'sans-serif',
	padding: '8px',
}

const estiloLetrero = {
	paddingTop: "20px",
	paddingRight: "12px",
	paddingLeft: "40px",
	paddingBottom: "1px"
}

const fondoBarraSuperior = {
	background: "#FFFFFF",
	padding: '30px'
}


function mapStateToProps(state) {
	return {
		usuarios: state.user.usuariosRegistrados,
		cedulaEditar: state.user.cedula,
		mensajeEditar: state.user.mensajeEditar
	}
}


export default withRouter(connect(mapStateToProps, { actionConsultarUsuarios, actionAsignarCedula, actualizarMensajeEditar, actionActualizarUsuarios })(ContenidoAdminUsuario));
