import React from 'react';
import { Redirect } from 'react-router-dom'

import Login from '../general/Login.js'

//redux conexion
import { connect } from 'react-redux';
import { actionConsultarUsuarios } from '../../actions/actionsUsuario.js'

class RedireccionLogin extends React.Component {

    state={
        inicio: false
    }

    cambiar=(objeto)=>{
        if(objeto==='e'){
            this.setState({inicio:true})
        }
    }

    render() {
        if(this.state.inicio){
            return <Redirect to='/inicio'/>;
        }

        return <Login cambiar={this.cambiar}/>
        
    }
}

function mapStateToProps(state) {

    return {
      users: state.user.list,
      token: state.user.token
    }
  }

export default connect(mapStateToProps, { actionConsultarUsuarios })(RedireccionLogin);
