import React from 'react';

import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import MaterialTable from 'material-table';


import { actionConsultarModulosAcceso } from '../../actions/actionsUsuario.js'
import { connect } from 'react-redux';

class ContenidoInicio extends React.Component {

	componentWillMount(){
		console.log('mods ',this.props.modulosAcceso);
	}

	componentDidMount() {
		this.props.actionConsultarModulosAcceso(localStorage.getItem('Token'));
	}

	render() {
		return (
			<>
				<Modal isOpen={true}
					toggle={this.toggle}
					className={this.props.className}
					size="col-md-6"
				>
					<ModalHeader className="center">Modulos disponibles</ModalHeader>
					<ModalBody>
						<MaterialTable
							title=""
							localization={{
								header: {
									actions: ' '
								},
								pagination: {
									nextTooltip: 'Siguiente ',
									previousTooltip: 'Anterior',
									labelDisplayedRows: '{from}-{to} de {count}',
									lastTooltip: 'Ultima pagina',
									firstTooltip: 'Primera pagina',
									labelRowsSelect: 'Registros',
									firstAriaLabel: 'oooo'
								},
								body: {
									emptyDataSourceMessage: 'Aun no hay ningun usuario registrado'
								},
								toolbar: {
									searchTooltip: 'Buscar',
									searchPlaceholder: 'Buscar'
								}
							}}
							columns={[
								{ title: 'Nombre', field: 'nombre', headerStyle: estiloCabecera, cellStyle: estiloFila },
								{ title: 'Modulo', field: 'moduloActividad', headerStyle: estiloCabecera, cellStyle: estiloFila }
							]}
							data={this.props.modulosAcceso}
							options={{
								search: true,
								rowStyle: estiloFila
							}}
							actions={[
								{
									icon: 'restore',
									tooltip: 'Suspender / Activar',
									onClick: (event, rowData) => this.activarDesactivarActividad(rowData.idActividad)
								}
							]}
						/>
						<ModalFooter>

						</ModalFooter>

					</ModalBody>
				</Modal>
			</>
		);
	}


}



const fondoBoton = {
	background: "#ec671d",
	fontSize: "14px",
	fontFamily: "Open sans, sans-serif"

}

const estiloLetrero = {
	paddingTop: "20px",
	paddingRight: "12px",
	paddingLeft: "40px",
	paddingBottom: "1px"
}

const fondoBarraSuperior = {
	background: "#FFFFFF"

}

const fondoTabla = {
	background: "#EAF2F2"
}

function mapStateToProps(state) {
	return {
		configuracion: state.conf.configuracion,
		modulosAcceso:state.user.modulosAcceso
	}
}

let formulario = reduxForm({
	form: 'inicioUsuario'
})(ContenidoInicio)

export default withRouter(connect(mapStateToProps, { actionConsultarModulosAcceso })(formulario));
