import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepButton from '@material-ui/core/StepButton';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { SketchPicker } from 'react-color';


export default class Configuracion extends React.Component {
	
	
	getSteps() {
		return ['Select campaign settings', 'Create an ad group', 'Create an ad', 'Create an ad group', 'Create an ad group'];
	}
	
	handleNext = () => {
		const newActiveStep =
			isLastStep() && !allStepsCompleted()
				? // It's the last step, but not all steps have been completed,
				// find the first step that has been completed
				getSteps().findIndex((step, i) => !(i in completed))
				: activeStep + 1;
		setActiveStep(newActiveStep);
	};

	getStepContent(step) {
		switch (step) {
			case 0:
				return (<SketchPicker disableAlpha={true} />)
			case 1:
				return 'Step 2: What is an ad group anyways?';
			case 2:
				return 'Step 3: This is the bit I really care about!';
			default:
				return 'Unknown step';
		}
	}

	render() {
		return (
			<div className={classes.root}>
				<Stepper nonLinear activeStep={activeStep}>
					{this.getSteps.map((label, index) => (
						<Step key={label}>
							<StepButton onClick={handleStep(index)} completed={completed[index]}>
								{label}
							</StepButton>
						</Step>
					))}
				</Stepper>
				<div>
					{allStepsCompleted() ? (
						<div>
							<Typography className={classes.instructions}>
								All steps completed - you&apos;re finished
				</Typography>
							<Button onClick={handleReset}>Reset</Button>
						</div>
					) : (
							<div>
								<Typography className={classes.instructions}>{this.getStepContent(activeStep)}</Typography>
								<div>
									<Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
										Back
				  </Button>
									<Button
										variant="contained"
										color="primary"
										onClick={handleNext}
										className={classes.button}
									>
										Next
				  </Button>
									{activeStep !== this.getSteps.length &&
										(completed[activeStep] ? (
											<Typography variant="caption" className={classes.completed}>
												Step {activeStep + 1} already completed
					  </Typography>
										) : (
												<Button variant="contained" color="primary" onClick={handleComplete}>
													{completedSteps() === this.getSteps.length - 1 ? 'Finish' : 'Complete Step'}
												</Button>
											))}
								</div>
							</div>
						)}
				</div>
			</div>
		);
	}
}

const [activeStep, setActiveStep] = React.useState(0);

const classes = useStyles();



const useStyles = makeStyles(theme => ({
	root: {
		width: '100%',
	},
	button: {
		marginRight: theme.spacing(1),
	},
	completed: {
		display: 'inline-block',
	},
	instructions: {
		marginTop: theme.spacing(1),
		marginBottom: theme.spacing(1),
	},
}));

const [completed, setCompleted] = React.useState({});




const completedSteps = () => {
	return Object.keys(completed).length;
};

const isLastStep = () => {
	return activeStep === totalSteps() - 1;
};

const allStepsCompleted = () => {
	return completedSteps() === totalSteps();
};



const handleBack = () => {
	setActiveStep(prevActiveStep => prevActiveStep - 1);
};

const handleStep = step => () => {
	setActiveStep(step);
};

const handleComplete = () => {
	const newCompleted = completed;
	newCompleted[activeStep] = true;
	setCompleted(newCompleted);
	handleNext();
};

const handleReset = () => {
	setActiveStep(0);
	setCompleted({});
};
