import React from "react";
import ReactDOM from "react-dom";

import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";

const styles = {
  inputRoot: {
    fontSize: 14
  },
  labelRoot: {
    fontSize: 14,
    color: "red",
    heigth:'3px',
    "&$labelFocused": {
      color: "purple",
      fontSize: 16
    }
  },
  labelFocused: {}
};
function App({ classes }) {
  return (
    <div className="App">
      <TextField
        id="standard-with-placeholder"
        label="Nombre"
        InputProps={{ classes: { root: {
          fontSize: 14
        } } }}
        InputLabelProps={{
          classes: {
            root:  {
              fontSize: 14,
              color: "red",
              heigth:'3px',
              "&$labelFocused": {
                color: "purple",
                fontSize: 16
              }},
            focused: {}
          }
        }}
        variant='outlined'
        margin="normal"
        size = 'small'
      />
    </div>
  );
}
export default withStyles(styles)(App);
