function RenderAlgoCorreo({
  input,
  label, classes, filas,
  meta: { touched, error },
  ...custom
}) {
  const classesP = useStyles();

  const [anchorEl, setAnchorEl] = React.useState(null);


  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);

  return (
    <>
      <Popover
        id="mouse-over-popover"
        className={classesP.popover}
        classes={{
          paper: classesP.paper,
        }}
        open={open}
        anchorEl={anchorEl}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'left',
        }}
        onClose={handlePopoverClose}
        disableRestoreFocus
      >
        <Typography className={classesP.typography}>Si se cambia el correo despues del registro el usuario tendra que recuperar la contraseña desde el inciar sesion </Typography>
      </Popover>
      <TextField
        fullWidth
        label={label}
        InputProps={{
          classes: { root: classes.inputRoot },
          endAdornment:
            <InputAdornment position="end">
              <IconButton
                edge="end"
                onClick={handlePopoverOpen}
              >
                <DeleteIcon />
              </IconButton>
            </InputAdornment>
        }}
        InputLabelProps={{
          classes: {
            root: classes.labelRoot,
            focused: classes.labelFocused
          }
        }}
        error={(touched && error) ? true : false}
        helperText={touched && error}
        required={true}
        {...input}
        {...custom}
        variant='outlined'
        margin="normal"
        size='small'
      />
    </>
  );
}

