import React from 'react';


import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';


//estilos
import '../../css/business-casual.css'
import '../../css/estilos.css'
import '../../css/bootstrap.min.css'
import '../../css/menu.css'
import 'react-notifications/lib/notifications.css';

import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { NotificationContainer } from 'react-notifications';
import { Field, reduxForm } from "redux-form";

class PopUpModulo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };
    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  handleChange = (event, input) => {
    event.preventDefault();
    let imageFile = event.target.files[0];
    if (imageFile) {
      const localImageUrl = URL.createObjectURL(imageFile);
      const imageObject = new window.Image();

      imageObject.onload = () => {
        imageFile.width = imageObject.naturalWidth;
        imageFile.height = imageObject.naturalHeight;
        input.onChange(imageFile);
        URL.revokeObjectURL(imageFile);
      };
      imageObject.src = localImageUrl;
      this.handlePreview(localImageUrl);
    }
  };

  handleSubmit = values => {
    console.log("Form Values: ", values);
  };

  onChange = (evento) => {
    console.log([evento.target.name]);
    this.setState({

      [evento.target.name]: evento.target.value

    });
    console.log(this.state);
  }


  render() {
    return (
      <div>
        <Button color="danger" className="btn btn-dark" style={fondoBoton} onClick={this.toggle}>Crear modulo +</Button>
        <Modal isOpen={this.state.modal}
          toggle={this.toggle}
          className={this.props.className}
          size="col-md-6"
        >
          <ModalHeader toggle={this.toggle} className="center">Crear modulo</ModalHeader>
          <ModalBody>
            <img src="sdsd" alt="preview"
              className="preview-image"
              style={{ height: "300px", objectFit: "cover" }} />
            <ul>
              <li>be JPEG or PNG</li>
              <li>have Width ≤ 100 px</li>
              <li>have Height ≤  200 px</li>
              <li>have Size ≤ 100kb</li>
            </ul>
            <form onSubmit={this.handleSubmit(this.handleSubmit)}>
              <Field
                name="image"
                type="file"
                validate={[
                  this.validateImageWeight,
                  this.validateImageWidth,
                  this.validateImageHeight,
                  this.validateImageFormat
                ]}
                component={this.renderFileInput}
              />

              <ModalFooter>
                <Button style={fondoBoton} type="submit">Registrar</Button>{''}
                <Button color="secondary" onClick={this.toggle}>Cancelar</Button>
              </ModalFooter>
            </form>
          </ModalBody>
        </Modal>
        <NotificationContainer />
      </div>
    );
  }
}

const renderFileInput = ({ input, type, meta }) => {
  const { mimeType } = this.props;
  return (
    <div>
      <input
        name={input.name}
        type={type}
        accept={mimeType}
        onChange={event => this.handleChange(event, input)}
      />
      {meta && meta.invalid && meta.error && (
        <div >{meta.error}</div>
      )}
    </div>
  );
};

const fondoBoton = {
  background: "#ec671d",
  fontSize: "13px",
  fontFamily: "sans-serif"

}


export default reduxForm({
  form: "formularioModulo"
})(PopUpModulo);
