import React from 'react';

//estilos
import '../../css/business-casual.css'
import '../../css/estilos.css'
import '../../css/bootstrap.min.css'
import '../../css/menu.css'

import { Button } from 'reactstrap';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import { withRouter } from 'react-router-dom';
import MaterialTable from 'material-table';
import Divider from '@material-ui/core/Divider';
import Alerta from '@icons/material/AlertIcon.js';

//componentes
import Barra from '../general/BarraDirecciones.js';

//redux
import { actionConsultarActividadesModulo } from '../../actions/actionsModulo'
import { connect } from 'react-redux';

class AsignarActividadModulo extends React.Component {

    componentDidMount() {
        if (this.props.codigoModulo === undefined || this.props.codigoModulo.length === 0) {
            this.props.history.push('/adminModulo');
        } else {
            this.props.actionConsultarActividadesModulo(this.props.codigoModulo, localStorage.getItem('Token'));
        }
    }

    componentDidUpdate(){
        console.log('actividades son: ',this.props.actividades)
    }

    render() {
        return (
            <div>
                <div class="text-left titulo" style={estiloLetrero}>
                    <h4>Asignar actividad a modulo</h4>
                </div>
                <Barra texto="Inicio > Asignación de actividad a modulo" />
                <div className="col-sm-12" style={{
                    paddingTop: "0px",
                    paddingRight: "40px",
                    paddingLeft: "40px",
                    paddingBottom: "0px",
                    margin: "0px 0px 30px"

                }}>
                    <div className="container shadow" style={fondoBarraSuperior}>
                        <form>
                            <br />
                            <label for="form_control_1">Seleccione la actividad que quiere asignar al modulo</label>
                            <div className="row">
                                <div className="col-md-4">
                                    <select className="bs-select form-control " id="selectDDL" onchange="redirige(this.value)" title="ACCIONES">
                                        <option value="0">Seleccione...</option>
                                        <option value="1">Crear usuario</option>
                                        <option value="2">Generar reporte</option>
                                        <option value="3">Crear modulo</option>
                                    </select>
                                </div>
                            </div>
                            <br />
                            <div>
                                <Button style={fondoBoton} onClick={this.onSubmit}>Guardar</Button>{''}
                                <Button style={fondoBotonS} color="secondary" onClick={this.toggle}>Cancelar</Button>
                            </div>
                        </form>
                        <br />
                    </div>
                </div>


                <div className="col-sm-12" style={{
                    paddingTop: "7px",
                    paddingRight: "40px",
                    paddingLeft: "40px",
                    paddingBottom: "20px",
                    margin: "0px 0px 32px"

                }}>
                    <div class="text-left" style={estiloTitulo}>
                        <h4>Actividades actualmente asignadas al modulo</h4>
                    </div>
                    <div className="container shadow" style={fondoBarraSuperior}>
                        <br />
                        <div className="jumbotron p-1 jumbotron-fluid" style={fondoTabla}>
                        <MaterialTable
                                        title="Actividades actualmente asignadas al usuario"
                                        localization={{
                                            header: {
                                                actions: ' '
                                            },
                                            pagination: {
                                                nextTooltip: 'Siguiente ',
                                                previousTooltip: 'Anterior',
                                                labelDisplayedRows: '{from}-{to} de {count}',
                                                lastTooltip: 'Ultima pagina',
                                                firstTooltip: 'Primera pagina',
                                                labelRowsSelect: 'Registros',
                                                firstAriaLabel: 'oooo'
                                            },
                                            body: {
                                                emptyDataSourceMessage: 'Aun no hay ningun modulo registrado'
                                            },
                                            toolbar: {
                                                searchTooltip: 'Buscar',
                                                searchPlaceholder: 'Buscar',
                                                nRowsSelected: '{0} actividades seleccionadas'
                                            }
                                        }}
                                        columns={[
                                            { title: 'Nombre de modulo', field: 'idActividad', headerStyle: estiloCabecera, cellStyle: estiloFila },
                                            { title: 'Descripcion del modulo', field: 'nombre', headerStyle: estiloCabecera, cellStyle: estiloFila }
                                        ]}
                                        data={this.props.actividades}
                                        options={{
                                            search: true,
                                            rowStyle: estiloFila,
                                            selection: true
                                        }}
                                        onSelectionChange={(rows) => {
                                            this.setState({ actividadesSeleccionadas: rows });
                                            console.log('You selected ', rows)
                                        }}
                                        actions={[
                                            {
                                                tooltip: 'Remove All Selected Users',
                                                icon: 'delete',
                                                onClick: (evt, data) => console.log("You want to delete ", data)
                                            }
                                        ]}
                                    />
                        </div>
                    </div>
                    <br />
                </div>
            </div>
        )
    }
}

const estiloTitulo = {

    paddingTop: "7px",
    paddingRight: "12px",
    paddingLeft: "5px",
    paddingBottom: "1px"
}

const estiloLetrero = {
    paddingTop: "20px",
    paddingRight: "12px",
    paddingLeft: "40px",
    paddingBottom: "1px"
}


const fondoBarraSuperior = {
    background: "#FFFFFF"

}

const fondoTabla = {
    background: "#EAF2F2"
}


const fondoBoton = {
    background: "#ec671d",
    fontSize: "14px",
    fontFamily: "Open sans, sans-serif"

}

const fondoBotonS = {
    background: "secondary",
    fontSize: "14px",
    fontFamily: "Open sans, sans-serif"

}

function mapStateToProps(state) {
    return {
        codigoModulo: state.mod.codigoModulo,
        actividades: state.mod.actividadesModulos
    }
}

export default withRouter(connect(mapStateToProps, {actionConsultarActividadesModulo})(AsignarActividadModulo));
