        // axios.get("http://localhost:9090/SuperadministradorESCOM-web/api/usu/")
        // .then(response => {
        //     dispatch({
        //         type: MOSTRAR_USUARIOS,
        //         respuesta: response.data
        //     });
        dispatch({
            type: MOSTRAR_CONFIGURACION,
            configuracion:[{
                fondoBarra: "red",
                fondoHover:"yellow"
            }] 
        });
    }
}
