import React from 'react';
import { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { getListPrograms, getProgramId } from '../../../redux/actions/programA.js';
import { ToastContainer } from 'react-toastify';
import MaterialTable from 'material-table';
import Add from './add.js';
import Edit from './edit.js';
import View from './view.js';

import EditIcon from '@material-ui/icons/Edit';



class ListPrograms extends Component {

    componentDidMount() {
        this.props.getListPrograms(localStorage.getItem('Token'))
    }

    save(id) {
        this.props.getProgramId(localStorage.getItem('Token'), id)
    }


    loadTable() {
        return this.props.listProgram.map((program) => {
            return (
                <tr key={program.id}>
                    <td>{program.name}</td>
                    <td>{program.levelEducation}</td>
                    <td>

                    </td>
                    <td>
                        <button onClick={() => this.save(program.id)} className="btn btn-sm text-light naranja" data-toggle="modal" data-target="#editModal">
                            <i class="fas fa-pen"></i>
                        </button>
                        <Edit />
                    </td>
                </tr>
            )
        })
    }

    render() {
        return (
            <div className="container " style={{ width: "90%" }}>

                <div className="text-left titulo">
                    <h4>Lista de programas</h4>
                    <br />
                </div>
                <div className="shadow" style={{ background: "#FFFFFF" }}>
                    <ToastContainer />
                    <MaterialTable
                        title=""
                        localization={{
                            header: {
                                actions: ' '
                            },
                            pagination: {
                                nextTooltip: 'Siguiente ',
                                previousTooltip: 'Anterior',
                                labelDisplayedRows: '{from}-{to} de {count}',
                                lastTooltip: 'Ultima pagina',
                                firstTooltip: 'Primera pagina',
                                labelRowsSelect: 'Registros',
                                firstAriaLabel: 'oooo'
                            },
                            body: {
                                emptyDataSourceMessage: 'Aun no hay ningun modulo registrado'
                            },
                            toolbar: {
                                searchTooltip: 'Buscar',
                                searchPlaceholder: 'Buscar'
                            }
                        }}
                        columns={[
                            { title: 'Nombre del programa', field:'name' },
                            { title: 'Nivel educativo', field: 'levelEducation'}
                        ]}
                        data={this.props.listProgram}
                        options={{
                            search: true
                        }}
                        actions={[
                            {
                                icon: (rowData) => {
                                    return (
                                        <div>
                                            <button onClick={() => this.save(rowData.id)}  data-toggle="modal" data-target="#viewModal">
                                                <EditIcon/>
                                            </button>
                                            <View />
                                        </div>
                                    )
                                },
                            },
                            {
                                icon: 'restore',
                                tooltip: 'Suspender / Activar',
                            },
                            {
                                icon: 'assignmentInd',
                                tooltip: 'Administrar actividades'
                            }
                        ]}

                    />
                    {/*}
                    <div>
                        <Add />
                    </div>
                    <table class="table border table-striped">
                        <thead class="colorBlue text-light">
                            <tr>
                                <th scope="col">Programa</th>
                                <th scope="col">Nivel Educativo</th>
                                <th scope="col"></th>
                                <th scope="col"></th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.loadTable()}
                        </tbody>
                    </table>
                    */}
                </div>
            </div>

        )
    }

}

function mapStateToProps(state) {
    return {
        listProgram: state.program.listProgramR,
        messageDisablePr: state.program.messageDisable

    }
}

export default withRouter(connect(mapStateToProps, { getListPrograms, getProgramId })(ListPrograms));
