import {
    ESTADO_MODULOS,MOSTRAR_MODULOS
} from '../actions/actionsModulo.js'


const initialState = {
    modulosRegistrados: [],
    estadoModulos: false
}

export function reducerUsuario(state = initialState, action) {
    switch (action.type) {
        case MOSTRAR_MODULOS:
            return Object.assign({}, state, { estadoModulos: action.respuesta })
        default:
            return state
    }
}
