export const required = value => {
    if (!value) {
        return 'Este campo es obligatorio *';
    }
}

export const thousand = value => {
    if (value !== undefined) {
        if (value.length > 1000) {
            return 'El limite es de 1000 caracteres';
        }
    }
}

export const fiveHundred = value => {
    if (value !== undefined) {
        if (value.length > 500) {
            return 'El limite es de 500 caracteres';
        }
    }
}

export const twoHundred = value => {
    if (value !== undefined) {
        if (value.length > 200) {
            return 'El limite es de 200 caracteres';
        }
    }
}

export const fifteen = value => {
    if (value !== undefined) {
        if (value.length > 15) {
            return 'El limite es de 15 caracteres';
        }
    }
}

export const minimum = value => {
    if (value !== undefined) {
        if (value.length < 3) {
            return 'ingrese mas de tres caracteres';
        }
    }
}

export const select = value => {
    if (value === '0' || value === undefined) {
        return 'Seleccione una opcion';
    }
};
