import React from 'react';
import { Redirect } from 'react-router-dom'

import Login from '../general/Login.js'

//redux conexion
import { connect } from 'react-redux';
import { actionRedireccionarLogin } from '../../actions/actionsUsuario.js'

class RedireccionLogin extends React.Component {
    render() {
        if(this.props.redireccion==='i'){
            return <Redirect to='/inicio'/>;
        }

        return <Login cambiar={this.cambiar}/>
        
    }
}

function mapStateToProps(state) {

    return {
        redireccion:state.user.redireccionLogin
    }
  }

export default connect(mapStateToProps, { actionRedireccionarLogin })(RedireccionLogin);
