import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepButton from '@material-ui/core/StepButton';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';


export default class Configuracion extends React.Component {

	state = {
		activeStep: 0,
		completed: {}
	}

	getSteps() {
		return ['Select campaign settings', 'Create an ad group', 'Create an ad'];
	}

	completedSteps = () => {
		return Object.keys(this.state.completed).length;
	};

	getStepContent(step) {
		switch (step) {
			case 0:
				return 'Step 1: Select campaign settings...';
			case 1:
				return 'Step 2: What is an ad group anyways?';
			case 2:
				return 'Step 3: This is the bit I really care about!';
			default:
				return 'Unknown step';
		}
	}

	allStepsCompleted = () => {
		return completedSteps() === totalSteps();
	};

	totalSteps = () => {
		return steps.length;
	};

	isLastStep = () => {
		return this.state.activeStep === totalSteps() - 1;
	};

	useStyles = makeStyles(theme => ({
		root: {
			width: '100%',
		},
		button: {
			marginRight: theme.spacing(1),
		},
		completed: {
			display: 'inline-block',
		},
		instructions: {
			marginTop: theme.spacing(1),
			marginBottom: theme.spacing(1),
		},
	}));

	render() {

		const handleNext = () => {
			const newActiveStep =
				isLastStep() && !allStepsCompleted()
					? // It's the last step, but not all steps have been completed,
					// find the first step that has been completed
					this.getSteps.findIndex((step, i) => !(i in completed))
					: activeStep + 1;
			setActiveStep(newActiveStep);
		};

		const handleBack = () => {
			setActiveStep(prevActiveStep => prevActiveStep - 1);
		};

		const handleStep = step => () => {
			setActiveStep(step);
		};

		const handleComplete = () => {
			const newCompleted = completed;
			newCompleted[activeStep] = true;
			setCompleted(newCompleted);
			handleNext();
		};

		const handleReset = () => {
			setActiveStep(0);
			setCompleted({});
		};


		return (
			<div className={this.useStyles.root}>
				<Stepper nonLinear activeStep={this.state.activeStep}>
					{this.getSteps.map((label, index) => (
						<Step key={label}>
							<StepButton onClick={handleStep(index)} completed={completed[index]}>
								{label}
							</StepButton>
						</Step>
					))}
				</Stepper>
				<div>
					{allStepsCompleted() ? (
						<div>
							<Typography className={this.useStyles.instructions}>
								All steps completed - you&apos;re finished
            </Typography>
							<Button onClick={handleReset}>Reset</Button>
						</div>
					) : (
							<div>
								<Typography className={this.useStyles.instructions}>{getStepContent(this.state.activeStep)}</Typography>
								<div>
									<Button disabled={this.state.activeStep === 0} onClick={handleBack} className={this.useStyles.button}>
										Back
              </Button>
									<Button
										variant="contained"
										color="primary"
										onClick={handleNext}
										className={classes.button}
									>
										Next
              </Button>
									{this.state.activeStep !== this.getSteps().length &&
										(completed[this.state.activeStep] ? (
											<Typography variant="caption" className={this.useStyles.completed}>
												Step {this.state.activeStep + 1} already completed
                  </Typography>
										) : (
												<Button variant="contained" color="primary" onClick={handleComplete}>
													{completedSteps() === totalSteps() - 1 ? 'Finish' : 'Complete Step'}
												</Button>
											))}
								</div>
							</div>
						)}
				</div>
			</div>
		);
	}
}
