import { MOSTRAR_CONFIGURACION, ACTUALIZAR_BARRALATERAL, ACTUALIZAR_BARRASUPERIOR } from '../actions/actionConfiguracion.js'


const initialState = {
    prueba: { fondoBarra: "", fondoSuperior: "" }
}

export function reducerConfiguracion(state = initialState, action) {
    switch (action.type) {
        case MOSTRAR_CONFIGURACION:
            return Object.assign({}, state, { prueba: action.configuracion })
        case ACTUALIZAR_BARRALATERAL:
            const actualizaBarraLateral = () => {
                return ({
                    fondoBarra: action.color,
                    fondoSuperior: state.fondoSuperior
                })
            }
            return Object.assign({}, state, { prueba: actualizaBarraLateral() })
        case ACTUALIZAR_BARRASUPERIOR:
            const actualizaBarraSuperior = () => {
                console.log('ESTADO BARRA L',this.state.fondoBarra);
                return ({
                    fondoBarra: state.fondoBarra,
                    fondoSuperior: action.color
                })
            }
            return Object.assign({}, state, { prueba: actualizaBarraSuperior() })
        default:
            return state
    }
}
