import React from 'react';

import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import MaterialTable from 'material-table';
import { withRouter } from 'react-router-dom';

import { actionConsultarModulosAcceso } from '../../actions/actionsUsuario.js'
import { connect } from 'react-redux';
import  useHistory from "react-router-dom";

class ContenidoInicio extends React.Component {

	componentDidUpdate() {
	}

	componentDidMount() {
		if (localStorage.getItem('Token') === '') {
			this.props.history.push('/');
		}
		this.props.actionConsultarModulosAcceso(localStorage.getItem('Token'));
	}

	onClickCancelar = (event) => {
		event.preventDefault();
		let history = useHistory();
		history.push("/adminUsuario");
		// console.log('history', this.props.history);
		// this.props.history.push('/adminUsuario');
	}

	render() {
		return (
			<>
				<Modal isOpen={true}
					toggle={this.toggle}
					className={this.props.className}
					size="col-md-6"
				>
					<ModalHeader className="center">Modulos disponibles</ModalHeader>
					<ModalBody>
						<MaterialTable
							title=""
							localization={{
								header: {
									actions: ' '
								},
								pagination: {
									nextTooltip: 'Siguiente ',
									previousTooltip: 'Anterior',
									labelDisplayedRows: '{from}-{to} de {count}',
									lastTooltip: 'Ultima pagina',
									firstTooltip: 'Primera pagina',
									labelRowsSelect: 'Registros',
									firstAriaLabel: 'oooo'
								},
								body: {
									emptyDataSourceMessage: 'Aun no hay ningun usuario registrado'
								},
								toolbar: {
									searchTooltip: 'Buscar',
									searchPlaceholder: 'Buscar'
								}
							}}
							columns={[
								{ title: 'Nombre', field: 'nombreModulo', headerStyle: estiloCabecera, cellStyle: estiloFila }
							]}
							data={this.props.modulosAcceso}
							options={{
								search: true,
								rowStyle: estiloFila
							}}
							actions={[
								{
									icon: 'AccountIcon',
									tooltip: 'Ir',
									onClick: (event, rowData) => {
										this.onClickCancelar(event);
									}
								}
							]}
						/>
						<ModalFooter>

						</ModalFooter>

					</ModalBody>
				</Modal>
			</>
		);
	}


}

const estiloCabecera = {
	fontSize: '13px',
	fontFamily: 'sans-serif',
	padding: '8px',
	background: '#e7ecf1'

}

const estiloFila = {
	fontSize: '12px',
	fontFamily: 'sans-serif',
	padding: '8px',
}


function mapStateToProps(state) {
	return {
		configuracion: state.conf.configuracion,
		modulosAcceso: state.user.modulosAcceso
	}
}

export default withRouter(connect(mapStateToProps, { actionConsultarModulosAcceso })(ContenidoInicio));
