import React from 'react';

//estilos
import '../../css/business-casual.css'
import '../../css/estilos.css'
import '../../css/bootstrap.min.css'
import '../../css/menu.css'

//componentes
import Barra from '../general/BarraDirecciones.js';
import { Button } from 'reactstrap';

//redux conexion
import { connect } from 'react-redux';
import { consultarConfiguracion } from '../../actions/actionConfiguracion.js'

class Configuracion extends React.Component {


    async componentDidMount() {
        this.props.consultarConfiguracion('esta es una prueba')
    }

    render() {
        return (
            <div>
                <div className="text-left titulo" style={estiloLetrero}>
                    <h4>Administrar usuarios</h4>
                </div>
                <Barra texto="Inicio > AdministraciÃ³n de usuarios" />
                <div className="col-sm-12" style={{
                    paddingTop: "20px",
                    paddingRight: "46px",
                    paddingLeft: "40px",
                    paddingBottom: "7px",
                }}>
                    <div className="input-group">
                        <input type="text" style={{ fontSize: "14px" }} className="form-control" placeholder="" />
                        <span className="input-group-btn">
                            <Button style={fondoBoton}>Buscar</Button>
                        </span>
                    </div>
                </div>
                <div className="container" style={{
                    paddingTop: "7px",
                    paddingRight: "12px",
                    paddingLeft: "40px",
                    paddingBottom: "20px",
                    margin: "0px 0px 32px"
                }}>
                    <div className="container shadow" style={fondoBarraSuperior}>
                        <br />
                        <br />
                        <div className="jumbotron p-1 jumbotron-fluid">
                            
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

const fondoBoton = {
	background: "#ec671d",
	fontSize: "14px",
	fontFamily: "Open sans, sans-serif"

}

const estiloLetrero = {
	paddingTop: "20px",
	paddingRight: "12px",
	paddingLeft: "40px",
	paddingBottom: "1px"
}

const fondoBarraSuperior = {
	background: "#FFFFFF"

}

const fondoTabla = {
	background: "#EAF2F2"
}


function mapStateToProps(state) {
    return {
        usuarios: state.prueba.prueba
    }
}


export default connect(mapStateToProps, { consultarConfiguracion })(Configuracion);
