import { MOSTRAR_CONFIGURACION, ACTUALIZAR_BARRALATERAL } from '../actions/actionConfiguracion.js'


const initialState = {
    prueba: []
}

export function reducerConfiguracion(state = initialState, action) {
    switch (action.type) {
        case MOSTRAR_CONFIGURACION:
            console.log("Llego el reducer conf", action.configuracion);
            return Object.assign({}, state, { prueba: action.configuracion })
        case ACTUALIZAR_BARRALATERAL:
            return Object.assign({}, state.prueba, { fondoBarra: action.color })
        default:
            return state
    }
}
