import React from 'react';

//estilos
import '../../css/business-casual.css'
import '../../css/estilos.css'
import '../../css/bootstrap.min.css'
import '../../css/menu.css'

//componentes
import { reduxForm, Field } from 'redux-form';
import { SketchPicker } from 'react-color';
import { Button } from 'reactstrap';
//redux conexion
import { connect } from 'react-redux';
import { consultarConfiguracion, actionActualizarBarraLateral } from '../../actions/actionConfiguracion.js'

class Configuracion extends React.Component {



    async componentDidMount() {
        this.props.consultarConfiguracion('esta es una prueba')
    }

    handleSubmit = formValues => {
        console.log("formularios ", formValues);
    }


    handleChangeComplete = (color) => {
        console.log('cambio', color);
    };

    render() {
        return (
            <div>
                <div className="text-left titulo" style={estiloLetrero}>
                    <h4>Administrar usuarios</h4>
                </div>
                <div className="container" style={{
                    paddingTop: "7px",
                    paddingRight: "12px",
                    paddingLeft: "40px",
                    paddingBottom: "20px",
                    margin: "0px 0px 32px"
                }}>
                    <div className="container shadow" style={fondoBarraSuperior}>
                        <br />
                        <br />
                        <div className="jumbotron p-1 jumbotron-fluid">
                            <form onSubmit={this.props.handleSubmit(this.handleSubmit)}>
                                <div className="contenedor-inputs">
                                    <div className="row">
                                        <div className="col-sm-12">
                                            <span>Barra lateral: </span>
                                            <br />
                                            <SketchPicker onChangeComplete={this.handleChangeComplete()} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        {/* <div className="col-sm-5">
                                            <span>Logo </span>
                                        </div>
                                        <div className="col-sm-7">
                                            <input type="file" name="files[]" multiple="" width="200" height="40" />
                                        </div> */}
                                    </div>
                                    <br />
                                    <div className="row">
                                        {/* <div className="col-sm-5">
                                            <span>Login </span>
                                        </div>
                                        <div className="col-sm-7">
                                            <input type="file" name="files[]" multiple="" width="200" height="40" />
                                        </div> */}
                                    </div>
                                    <br />
                                </div>
                                <Button style={{ background: "blue" }} type="submit">Registrar</Button>{''}
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

const generarInput = ({ input, label, type, meta: { touched, error, warning } }) => (
    <div>
        <div>
            <input {...input} placeholder={label} type={type} className="form-control letra form-control-solid placeholder-no-fix" />
            {touched && ((error && <span className="text-danger letra form-group">{error}</span>) || (warning && <span>{warning}</span>))}
        </div>
    </div>
)

const validate = values => {
    const errors = {}
    if (!values.nombre) {
        errors.nombre = 'Este campo es obligatorio'
    } else if (values.nombre.length < 2) {
        errors.nombre = 'Minimum be 2 characters or more'
    }

    if (!values.email) {
        errors.email = 'Required'
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        errors.email = 'Invalid email address'
    }
    return errors
}

const fondoBoton = {
    background: "#ec671d",
    fontSize: "14px",
    fontFamily: "Open sans, sans-serif"

}

const estiloLetrero = {
    paddingTop: "20px",
    paddingRight: "12px",
    paddingLeft: "40px",
    paddingBottom: "1px"
}

const fondoBarraSuperior = {
    background: "#FFFFFF"

}

const fondoTabla = {
    background: "#EAF2F2"
}


function mapStateToProps(state) {
    return {
        configuracion: state.prueba
    }
}

let formulario = reduxForm({
    form: 'registrarConfiguracion', validate
})(Configuracion)

export default connect(mapStateToProps, { consultarConfiguracion, actionActualizarBarraLateral })(formulario);
