import React from 'react';

//estilos
import '../../css/business-casual.css'
import '../../css/estilos.css'
import '../../css/bootstrap.min.css'
import '../../css/menu.css'


//componentes
import Barra from '../general/BarraDirecciones.js'
import PopUpUsuario from '../popup/PopUpUsuario.js'
import Fila from '../general/FilaTablaUsuario.js'
import { Button } from 'reactstrap';
import Paginator from "../../utilitario/Paginator.js";
import { pagination, filteredItem } from "../../utilitario/DataTable.js";

//redux conexion
import { connect } from 'react-redux';
import { actionConsultarUsuarios } from '../../actions/actionsUsuario.js'

class ContenidoAdminUsuario extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			todoList: {},
			todoListRecords: [],
			filterText: ""
		};
		this.onPaginate = this.onPaginate.bind(this);
		this.onFilter = this.onFilter.bind(this);
		this.generateTB = this.generateTB.bind(this);
	}

	onPaginate(currentPage) {
		let todoList = { ...this.state.todoList };
		let paginatedTodoList = pagination(
			currentPage,
			5,
			this.state.todoListRecords
		);
		todoList.pages = paginatedTodoList.pages;
		todoList.paginatedData = paginatedTodoList.paginatedData;
		todoList.paginatorData = paginatedTodoList.paginatorData;
		this.setState({ todoList });
	}

	onFilter(filterText) {
		this.setState({ filter: filterText });
		let todoList = { ...this.state.todoList };
		if (filterText.length) {
			let filteredTodoList = filteredItem(
				filterText,
				"title",
				5,
				this.state.todoListRecords
			);
			todoList.pages = filteredTodoList.pages;
			todoList.paginatedData = filteredTodoList.paginatedData;
			todoList.paginatorData = filteredTodoList.paginatorData;
		} else {
			console.log(this.state.todoList)
			let todoListRecords = [...this.state.todoListRecords];
			let paginatedTodoList = pagination(1, 5, todoListRecords);
			todoList.pages = paginatedTodoList.pages;
			todoList.paginatedData = paginatedTodoList.paginatedData;
			todoList.paginatorData = paginatedTodoList.paginatorData;
		}
		this.setState({
			todoList
		});
	}

	generateTB() {
		return this.props.usuarios.map((todo, index) => (
			<tr key={todo.numeroDocumento}>
				<td>{todo.numeroDocumento}</td>
				<td>{todo.nombre}</td>
				<td>{todo.correoElectronico}</td>
			</tr>
		));
	}


	async componentDidUpdate(){
		let todoList = await pagination(1, 5, this.props.usuarios);
		this.setState({
			todoList,
			todoListRecords: this.props.usuarios
		});
	}

	async componentDidMount() {
		this.props.actionConsultarUsuarios(localStorage.getItem('Token'));
		
	}

	renderTableData() {
		return this.props.usuarios.map((usuario, index) => {
			const { cedula } = usuario //destructuring
			return (
				<Fila usuario={usuario} cambiar={this.props.cambiar} key={cedula} />
			)
		})

	}

	render() {
		return (
			<div>
				<div className="text-left titulo" style={estiloLetrero}>
					<h4>Administrar usuarios</h4>
				</div>
				<Barra texto="Inicio > AdministraciÃ³n de usuarios" />
				<div className="col-sm-12" style={{
					paddingTop: "20px",
					paddingRight: "46px",
					paddingLeft: "40px",
					paddingBottom: "7px",
				}}>
					<div className="input-group">
						<input type="text" style={{ fontSize: "14px" }} className="form-control" placeholder="" />
						<span className="input-group-btn">
							<Button style={fondoBoton}>Buscar</Button>
						</span>
					</div>
				</div>
				<div className="container" style={{
					paddingTop: "7px",
					paddingRight: "12px",
					paddingLeft: "40px",
					paddingBottom: "20px",
					margin: "0px 0px 32px"
				}}>
					<div className="container shadow" style={fondoBarraSuperior}>
						<br />
						<PopUpUsuario funcion={this.anadirTarea} />
						<br />
						<div className="jumbotron p-1 jumbotron-fluid" style={fondoTabla}>
							<div className="row">
								<div className="col-sm-12">
									<div className="form-group">
										<label htmlFor="filterData">Search</label>
										<input
											type="text"
											className="form-control"
											id="filterData"
											aria-describedby="filterHelp"
											placeholder="Search for data..."
											onChange={event => this.onFilter(event.target.value)}
										/>
										<small id="filterHelp" className="form-text text-muted">
											Find any data you want...
            </small>
									</div>
									<div className="table-responsive">
										<table className="table">
											<thead className="thead-dark">
												<tr>
													<th scope="col">ID</th>
													<th scope="col">USER ID</th>
													<th scope="col">TITLE</th>
												</tr>
											</thead>
											<tbody>
												{Object.keys(this.state.todoList).length !== 0 &&
													this.state.todoList.constructor === Object
													? this.generateTB()
													: null}
											</tbody>
										</table>
									</div>
									{Object.keys(this.state.todoList).length !== 0 &&
										this.state.todoList.constructor === Object ? (
											<Paginator
												onPaginate={this.onPaginate}
												paginatorData={this.state.todoList.paginatorData}
												pages={this.state.todoList.pages}
											/>
										) : null}
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}


}

const fondoBoton = {
	background: "#ec671d",
	fontSize: "14px",
	fontFamily: "Open sans, sans-serif"

}

const estiloLetrero = {
	paddingTop: "20px",
	paddingRight: "12px",
	paddingLeft: "40px",
	paddingBottom: "1px"
}

const fondoBarraSuperior = {
	background: "#FFFFFF"

}

const fondoTabla = {
	background: "#EAF2F2"
}

function mapStateToProps(state) {
	return {
		usuarios: state.user.usuariosRegistrados
	}
}


export default connect(mapStateToProps, { actionConsultarUsuarios })(ContenidoAdminUsuario);
